<?php namespace OpenEMR\Common{class Checker{private static$minimumPhpVersion="5.6.0";private static function xlDelegate($value){if(function_exists("xl")){return xl($value);}return$value;}public static function checkPhpVersion(){$phpCheck=self::isPhpSupported();$response="";if(!$phpCheck){$response.=self::xlDelegate("PHP version needs to be at least")." ".self::$minimumPhpVersion.".";}else{$response=true;}return$response;}private static function isPhpSupported(){return version_compare(phpversion(),self::$minimumPhpVersion,">=");}}}